do
	echo "cd /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat; /public/software/histat2/hisat2-2.1.0/hisat2 -p 10 -x /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/library/mhl.genome -1 /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/01.fastp/01.fastp/${i}_fastp_1.fq.gz -2 /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/01.fastp/01.fastp/${i}_fastp_2.fq.gz -S /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.sam && samtools view -@ 8 -bS /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.sam > /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.bam && samtools sort -@ 8 /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.bam -o /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.sorted.bam && samtools index /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.sorted.bam && rm /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.sam /data02/zhangfenglei/project/08.telomere/01.ABPC/01.RNA-seq/02.hisat/01.hisat/${i}.mhl.bam" > 01.hisatqs/${i}.mhl.sh
